package day2;

public class DataTypes2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20;
		System.out.println("The value of a :"+a+"\nThe value of b :"+b);
		int c,d;
		c=300;
		d=400;
		System.out.println("The value of c :"+c+"\nThe value of d :"+d);
		long e=100000;
		System.out.println("The value of e :"+e);
		short f=-100;
		System.out.println("The value of f :"+f);
		double g=123456;
		System.out.println("The value of g :"+g);
		float h=10.23f;
		System.out.println("The value of h :"+h);
		char i='A';
		System.out.println("The value of i :"+i);
		boolean j=true;
		System.out.println("The value of j :"+j);
		String k="Sriram";// Double characters
		System.out.println("The value of k :"+k);
		String l="S";// Single character
		System.out.println("The value of l :"+l);
		//char m='ABC'; // Double chars doesn't works
				
		
		
	}
}
